import React, {useState, useEffect} from "react"
import NavBar from "./NavBar"
import axios from "axios"


function getHistory(){
    var username = JSON.parse(localStorage.getItem("user")).username
    return axios
        .post("http://localhost:8080/api/auth/getHistory", {
            username     
        })
        .then(response => {
                localStorage.setItem("formData", JSON.stringify(response.data.data))
            return response.data
        })
}

function fuelInfo() {
    var username = JSON.parse(localStorage.getItem("user")).username
    return axios.post("http://localhost:8080/api/auth/fuelInfo", {
        username
    }).then(response => {
            if(response.data){
                localStorage.setItem("address", response.data.address)
            }
            return response.data
        });
}

export default function History(){
    var [gallons, setGallons] = useState();
    var [address, setAddress] = useState();
    var [dates, setDates] = useState();
    var [prices, setPrices] = useState();
    var [amountDues, setAmountDues] = useState();

    useEffect(() => {
        fuelInfo().then(
            () => { 
                setAddress(localStorage.getItem("address"))
            },
            error => {
                const resMessage = (
                    error.response &&
                    error.response.data &&
                    error.response.data.message) ||
                    error.message ||
                    error.toString()

                alert(error.response.data.resMessage)
        })

        const afunct = async() =>{
            getHistory().then(
                () => { 
                    setValues(localStorage.getItem("formData"))
                    setValues(localStorage.getItem("formData"))
                    alert(values)
                    var gallons1 = values.map((index) =>(
                        <tr>{index.gallons}</tr>
                    ))
                    var dates1 = values.map((index) =>(
                        <tr>{index.date}</tr>
                    ))
                    var prices1 = values.map((index) =>(
                        <tr>{index.suggested}</tr>
                    ))
                    var amountDues1 = values.map((index) =>(
                        <tr>{index.total}</tr>
                    ))
                    alert("values" + JSON.stringify(values))
                    setGallons(gallons1)
                    setDates(dates1)
                    setPrices(prices1)
                    setAmountDues(amountDues1)
                    alert(JSON.stringify(gallons))
                    alert(JSON.stringify(gallons1))
                },
                error => {

                    const resMessage = (
                        error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                        error.message ||
                        error.toString()

                    alert(error.response.data.resMessage)
            })
        }
        afunct();
    },[]);
    return(
        <div className="Login">
            <NavBar />
            {!gallons && <h2>No history yet!</h2>}
            <table>
                    <tbody>
                    <tr>
                        <td>Gallons</td>
                        <td>Address</td>
                        <td>Delivery Date</td>
                        <td>Price per gallon</td>
                        <td>Amount Due</td>
                    </tr>
                    <td>{gallons}</td>
                    <td>{address}</td>
                    <td>{dates}</td>
                    <td>{prices}</td>
                    <td>{amountDues}</td>
                </tbody>
            </table>
        </div>
    )
}
